mapper.select:
SELECT id
FROM team_info
WHERE name = #{name}

mapper.update:
UPDATE team_info
SET use_yn = 'N'
WHERE id = #{teamId}

UPDATE team_info
SET modified_timestamp = NOW()

    , name = #{name}


    , description = #{description}

WHERE id = #{id}

UPDATE play_time_info
SET  from_time = #{playTimeFrom}
  ,to_time = #{playTimeTo}
  ,day_of_week = #{dayOfWeek}
WHERE id = #{id}
  AND team_id = #{teamId}

UPDATE hometown_info
SET  jibun_address = #{jibunAddress}
  ,road_address = #{roadAddress}
  ,latitude = #{latitude}
  ,longitude = #{longitude}
  ,legal_code = #{legalCode}
WHERE id = #{id}
  AND team_id = #{teamId}

UPDATE team_user_mapping
SET role_code = #{role}
WHERE team_id = #{teamId}
  AND user_id = #{userId}
  AND use_yn = 'Y'
